import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom';
import '../styles/Appointments.css'; // Custom styles

export default function Appointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ pet: '', doctor: '', date: '', service: '', price: '' });
  const navigate = useNavigate();

  const fetchAppointments = async () => {
    try {
      const res = await axios.get('/appointments', {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setAppointments(res.data);
    } catch (err) {
      console.error('‚ùå Fetch error:', err.response?.data || err.message);
    }
  };

  useEffect(() => {
    if (user?.token) fetchAppointments();
  }, [user]);

  const handleSubmit = async () => {
    try {
      await axios.post('/appointments', form, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      alert('‚úÖ Appointment created');
      setForm({ pet: '', doctor: '', date: '', service: '', price: '' });
      fetchAppointments();
    } catch (err) {
      console.error('‚ùå Submit error:', err.response?.data || err.message);
      alert('Failed to save appointment');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this appointment?')) return;
    try {
      await axios.delete(`/appointments/${id}`, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      fetchAppointments();
    } catch (err) {
      console.error('‚ùå Delete error:', err.response?.data || err.message);
      alert('Failed to delete appointment');
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-sky-100 via-rose-50 to-indigo-100">
      <div className="flex-grow p-6 max-w-4xl mx-auto w-full">
        <h2 className="text-4xl font-extrabold text-center text-indigo-800 mb-8 drop-shadow">üêæ Appointments</h2>

        {/* Form */}
        {user?.role !== 'admin' && (
          <div className="form-box space-y-3 mb-10 bg-white/80 backdrop-blur-md p-6 rounded-2xl shadow-xl">
            <h3 className="text-lg font-semibold text-indigo-600 mb-2">+ Create Appointment</h3>
            <input placeholder="üê∂ Pet name" value={form.pet} onChange={e => setForm({ ...form, pet: e.target.value })} className="p-3 border rounded-xl w-full" />
            <input placeholder="üë®‚Äç‚öïÔ∏è Doctor" value={form.doctor} onChange={e => setForm({ ...form, doctor: e.target.value })} className="p-3 border rounded-xl w-full" />
            <input type="datetime-local" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} className="p-3 border rounded-xl w-full" />
            <input placeholder="üíâ Service" value={form.service} onChange={e => setForm({ ...form, service: e.target.value })} className="p-3 border rounded-xl w-full" />
            <input type="number" placeholder="üí∞ Price" value={form.price} onChange={e => setForm({ ...form, price: e.target.value })} className="p-3 border rounded-xl w-full" />
            <button onClick={handleSubmit} className="bg-indigo-600 text-white px-6 py-3 rounded-xl hover:bg-indigo-700 transition transform hover:-translate-y-1">
              Save Appointment
            </button>
          </div>
        )}

        {/* Appointment Cards */}
        <ul className="space-y-5">
          {appointments.map(app => (
            <li key={app._id} className="appointment-card p-6 rounded-2xl shadow-md hover:shadow-xl transition">
              <div>
                <h4 className="text-xl font-semibold text-indigo-700">
                  üêï {app.pet} <span className="text-sm text-gray-500 ml-2">{new Date(app.date).toLocaleString()}</span>
                </h4>
                <p className="text-gray-700 mt-1">
                  Service: <strong>{app.service}</strong> <br />
                  Doctor: <span className="text-gray-800">{app.doctor}</span>
                </p>
                {user?.role === 'admin' && app.user && (
                  <div className="mt-2 text-sm text-gray-600">
                    üë§ <strong>{app.user.name}</strong> ‚Äî {app.user.email}
                    <span className="admin-badge ml-2">Pet Owner</span>
                  </div>
                )}
              </div>
              <div className="space-x-3 mt-4">
                <button
                  onClick={() => navigate(`/appointments/edit/${app._id}`)}
                  className="px-4 py-2 bg-yellow-400 hover:bg-yellow-500 text-white rounded-lg transition shadow"
                >
                  ‚úèÔ∏è Edit
                </button>
                <button
                  onClick={() => handleDelete(app._id)}
                  className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-lg transition shadow"
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <Footer />
    </div>
  );
}
