import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';

export default function Appointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ pet: '', doctor: '', date: '', service: '', price: '' });
  const [editingId, setEditingId] = useState(null);

  const fetchAppointments = async () => {
    try {
      const res = await axios.get('/appointments', {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setAppointments(res.data);
    } catch (err) {
      console.error('❌ Fetch error:', err.response?.data || err.message);
    }
  };

  useEffect(() => {
    if (user?.token) fetchAppointments();
  }, [user]);

  const handleSubmit = async () => {
    try {
      if (editingId) {
        await axios.put(`/appointments/${editingId}`, form, {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        alert('✅ Appointment updated');
      } else {
        await axios.post('/appointments', form, {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        alert('✅ Appointment created');
      }
      setForm({ pet: '', doctor: '', date: '', service: '', price: '' });
      setEditingId(null);
      fetchAppointments();
    } catch (err) {
      console.error('❌ Submit error:', err.response?.data || err.message);
      alert('Failed to save appointment');
    }
  };

  const handleEdit = (app) => {
    setForm({
      pet: app.pet,
      doctor: app.doctor,
      date: app.date?.substring(0, 16),
      service: app.service,
      price: app.price
    });
    setEditingId(app._id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this appointment?')) return;
    try {
      await axios.delete(`/appointments/${id}`, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      fetchAppointments();
    } catch (err) {
      console.error('❌ Delete error:', err.response?.data || err.message);
      alert('Failed to delete appointment');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Appointments</h2>

      {user?.role !== 'admin' && (
        <div className="space-y-2 mb-6">
          <input placeholder="Pet" value={form.pet} onChange={e => setForm({ ...form, pet: e.target.value })} className="p-2 border rounded w-full" />
          <input placeholder="Doctor" value={form.doctor} onChange={e => setForm({ ...form, doctor: e.target.value })} className="p-2 border rounded w-full" />
          <input type="datetime-local" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} className="p-2 border rounded w-full" />
          <input placeholder="Service" value={form.service} onChange={e => setForm({ ...form, service: e.target.value })} className="p-2 border rounded w-full" />
          <input type="number" placeholder="Price" value={form.price} onChange={e => setForm({ ...form, price: e.target.value })} className="p-2 border rounded w-full" />
          <button onClick={handleSubmit} className="bg-blue-600 text-white px-4 py-2 rounded">
            {editingId ? 'Update' : 'Add'} Appointment
          </button>
        </div>
      )}

      <ul className="space-y-2">
        {appointments.map(app => (
          <li key={app._id} className="border p-4 rounded shadow-sm">
            <div>
              <strong>{app.pet}</strong> - {app.service} with {app.doctor} on {new Date(app.date).toLocaleString()}
              {user?.role === 'admin' && app.user && (
                <div className="text-sm text-gray-600">
                  Owner: {app.user.name} ({app.user.email})
                </div>
              )}
            </div>
            <div className="space-x-2 mt-2">
              <button onClick={() => handleEdit(app)} className="px-3 py-1 bg-yellow-500 text-white rounded">Edit</button>
              <button onClick={() => handleDelete(app._id)} className="px-3 py-1 bg-red-600 text-white rounded">Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
