import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom';
import '../styles/Appointments.css'; // Enhanced styles

export default function Appointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [form, setForm] = useState({ pet: '', doctor: '', date: '', service: '', price: '' });
  const navigate = useNavigate();

  const fetchAppointments = async () => {
    try {
      const res = await axios.get('/appointments', {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setAppointments(res.data);
    } catch (err) {
      console.error('❌ Fetch error:', err.response?.data || err.message);
    }
  };

  useEffect(() => {
    if (user?.token) fetchAppointments();
  }, [user]);

  const handleSubmit = async () => {
    try {
      await axios.post('/appointments', form, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      alert('✅ Appointment created');
      setForm({ pet: '', doctor: '', date: '', service: '', price: '' });
      fetchAppointments();
    } catch (err) {
      console.error('❌ Submit error:', err.response?.data || err.message);
      alert('Failed to save appointment');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this appointment?')) return;
    try {
      await axios.delete(`/appointments/${id}`, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      fetchAppointments();
    } catch (err) {
      console.error('❌ Delete error:', err.response?.data || err.message);
      alert('Failed to delete appointment');
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-indigo-100 to-pink-100">
      <div className="flex-grow p-6 max-w-4xl mx-auto w-full">
        <h2 className="text-3xl font-bold text-center text-indigo-800 mb-6">Appointments</h2>

        {/* Form */}
        {user?.role !== 'admin' && (
          <div className="space-y-3 mb-10 bg-white/80 backdrop-blur-md p-6 rounded-xl shadow-md">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">Create Appointment</h3>
            <input placeholder="Pet" value={form.pet} onChange={e => setForm({ ...form, pet: e.target.value })} className="p-3 border rounded-lg w-full" />
            <input placeholder="Doctor" value={form.doctor} onChange={e => setForm({ ...form, doctor: e.target.value })} className="p-3 border rounded-lg w-full" />
            <input type="datetime-local" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} className="p-3 border rounded-lg w-full" />
            <input placeholder="Service" value={form.service} onChange={e => setForm({ ...form, service: e.target.value })} className="p-3 border rounded-lg w-full" />
            <input type="number" placeholder="Price" value={form.price} onChange={e => setForm({ ...form, price: e.target.value })} className="p-3 border rounded-lg w-full" />
            <button onClick={handleSubmit} className="bg-blue-600 text-white px-5 py-2 rounded-lg hover:bg-blue-700 transition">
              Add Appointment
            </button>
          </div>
        )}

        {/* Appointment Cards */}
        <ul className="space-y-4">
          {appointments.map(app => (
            <li key={app._id} className="appointment-card p-5 shadow-md">
              <div>
                <strong className="text-indigo-800">{app.pet}</strong> – {app.service} with <span className="font-medium">{app.doctor}</span> <br />
                <span className="text-gray-600 text-sm">on {new Date(app.date).toLocaleString()}</span>
                {user?.role === 'admin' && app.user && (
                  <div className="mt-1 text-sm text-gray-500">
                    Owner: {app.user.name} ({app.user.email})
                  </div>
                )}
              </div>
              <div className="space-x-2 mt-3">
                <button
                  onClick={() => navigate(`/appointments/edit/${app._id}`)}
                  className="px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 transition"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(app._id)}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <Footer />
    </div>
  );
}
