import React, { useEffect, useState } from 'react';
import axios from '../api/axios';


export default function AdminUsers() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/users', {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setUsers(res.data);
    } catch (err) {
      console.error('❌ Failed to fetch users:', err.response?.data || err.message);
      alert('Failed to load users');
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`/users/${id}`, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setUsers(users.filter(u => u._id !== id));
      alert('✅ User deleted');
    } catch (err) {
      console.error('❌ Failed to delete user:', err.response?.data || err.message);
      alert('Failed to delete user');
    }
  };

  useEffect(() => {
    if (user?.token) {
      fetchUsers();
    }
  }, [user]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">All Users</h2>
      <ul className="space-y-2">
        {users.map(u => (
          <li key={u._id} className="p-4 border rounded flex justify-between items-center">
            <div>{u.name} ({u.email}) - {u.role}</div>
            <button onClick={() => deleteUser(u._id)} className="bg-red-500 text-white px-3 py-1 rounded">
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
