import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between">
      <div className="space-x-4">
        <Link to="/" className="hover:underline">Home</Link>
        {user?.role === 'user' && <Link to="/appointments" className="hover:underline">Appointments</Link>}
        {user?.role === 'admin' && <Link to="/admin/users" className="hover:underline">Admin</Link>}
      </div>
      {user ? (
        <button onClick={handleLogout} className="bg-red-600 px-3 py-1 rounded">Logout</button>
      ) : (
        <div className="space-x-2">
          <Link to="/login" className="hover:underline">Login</Link>
          <Link to="/register" className="hover:underline">Register</Link>
        </div>
      )}
    </nav>
  );
}
