import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';

export default function EditAppointment() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [form, setForm] = useState({ pet: '', doctor: '', date: '', service: '', price: '' });

  useEffect(() => {
    const fetchAppointment = async () => {
      try {
        const res = await axios.get(`/appointments/${id}`, {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        const app = res.data;
        setForm({
          pet: app.pet,
          doctor: app.doctor,
          date: app.date?.substring(0, 16),
          service: app.service,
          price: app.price
        });
      } catch (err) {
        console.error('Error loading appointment:', err);
        alert('Failed to load appointment');
      }
    };

    fetchAppointment();
  }, [id, user.token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/appointments/${id}`, form, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      alert('✅ Appointment updated');
      navigate('/appointments');
    } catch (err) {
      console.error('Update error:', err);
      alert('Failed to update appointment');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Edit Appointment</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          placeholder="Pet"
          value={form.pet}
          onChange={e => setForm({ ...form, pet: e.target.value })}
          className="p-2 border rounded w-full"
        />
        <input
          placeholder="Doctor"
          value={form.doctor}
          onChange={e => setForm({ ...form, doctor: e.target.value })}
          className="p-2 border rounded w-full"
        />
        <input
          type="datetime-local"
          value={form.date}
          onChange={e => setForm({ ...form, date: e.target.value })}
          className="p-2 border rounded w-full"
        />
        <input
          placeholder="Service"
          value={form.service}
          onChange={e => setForm({ ...form, service: e.target.value })}
          className="p-2 border rounded w-full"
        />
        <input
          type="number"
          placeholder="Price"
          value={form.price}
          onChange={e => setForm({ ...form, price: e.target.value })}
          className="p-2 border rounded w-full"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Update Appointment
        </button>
      </form>
    </div>
  );
}
